CREATE TABLE `php_mysql_parcial`.`inventario` ( `id` INT NOT NULL AUTO_INCREMENT , `product_name` VARCHAR(20) NOT NULL , `product_brand` VARCHAR(50) NOT NULL , `product_size` INT NULL DEFAULT '0' , `units` INT NOT NULL , `buy_price` FLOAT NOT NULL , `earn` FLOAT NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `measure_unit` ENUM('KG','L') NULL DEFAULT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
